</template>

<script>
export default {
  metaInfo: {
    title: 'Home',
    bodyAttrs: {
      class: 'page--home'
    }
  },
  mounted() {
    let externalScript = document.createElement('script')
    externalScript.setAttribute('src', 'https://static.mailerlite.com/js/w/webforms.min.js?v0c75f831c56857441820dcec3163967c')
    document.head.appendChild(externalScript)
  },
  methods: {
    ml_webform_success_4538002() {
      var r=ml_jQuery||jQuery;r(".ml-subscribe-form-4538002 .row-success").show(),r(".ml-subscribe-form-4538002 .row-form").hide()
    },
	  truncateExcerpt (ex, num) {
		  if (ex.length <= num) {
			  return ex;
		  } else {
			  return `${ex.slice(0,num)}...`;
		  }
	  },
	  listCategories (categories) {
		  var catList = categories.map((cat) => {
			  return cat.title;
		  })
		  return catList.join(', ').replace(/-/g, ' ');
	  }
  }
}
</script>

<page-query>
  query {
    posts: allPost (limit: 4) {
      edges {
        node {
					id
          slug
					title 
					path 
					excerpt
					content
					timeToRead(speed: 200)
					categories {
            title
          }
          tags {
            title
          }
        } 
      } 
    } 
  }
</page-query>